using UnityEngine;
using System.Collections;
namespace com.stanwind.unity3d{
	public class Stan:MonoBehaviour{
		public static Stan _ins;
		public StanImpl stanUtils;
		public GameObject stan;

		void Awake(){
			#if UNITY_IOS
				stanUtils = new IOSImpl();
			#elif UNITY_ANDROID
				stanUtils = new AndrImpl();
			#endif

			_ins = this;
			//新建监听Obj
			stan = new GameObject ("Stan");
			//增加监听脚本组件
			stan.AddComponent<StanListener>();
		}

		public void openPhotoLibrary(){
			#if UNITY_ANDROID
				stanUtils.OpenPhoto();
			#elif UNITY_EDITOR
				Debug.Log("测试图片处理");
				//测试图片处理
				stan.GetComponent<StanListener>().PhotoCallBack("");
			#else
				stanUtils.OpenPhoto ();
			#endif
		}
	}
}